package ifelse;

public class ifelsegreatestof3numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=50;
		int b=100;
		int c=20;
		if (a>b && a>c)
		{
			System.out.println("a is greatest");
		}
		else if (b>a && b>c)
		{
			System.out.println("b is greatest");
		}
		else
		{System.out.println("c is greatest");
		}
	}

}
