package loops;

public class multiplication5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5; //datatype variable value
		for (int i=1;i<=10;i++) //datatype condition expression and loop until it reaches 10
		{
			System.out.println(n+"x" +i+"="+(n*i)); //".." just lets you print what is in quotes
		}
	}

}
